CREATE TABLE CarInfo (
    CarID INT PRIMARY KEY AUTO_INCREMENT,
    Brand VARCHAR(50),
    Model VARCHAR(50),
    Year INT,
    BodyType VARCHAR(30),
    Price DECIMAL(10,2)
);

select * from carinfo

CREATE TABLE CarSpecs (
    SpecID INT PRIMARY KEY AUTO_INCREMENT,
    CarID INT,
    FuelType VARCHAR(20),
    Transmission VARCHAR(20),
    Horsepower INT,
    EngineSize DECIMAL(3,1),
    Mileage INT,
    FOREIGN KEY (CarID) REFERENCES CarInfo(CarID)
);

